<script id="comment-detail" type="text/x-kendo-template">
    <span style="word-wrap: break-word;"> - #: CommentText #</span><br>
    <hr>
</script>

<script>
    var Config = {
        crudApi: `${ENV.restApi}`,
        templateApi: `${ENV.templateApi}`,
        observable: {
        },
        id: '<?= $this->input->get("id") ?>'
    };
    var Detail = function() {
        var observable = Object.assign(Config.observable, {
            item: {},
        });

        return {
            model: {},
            read: async function() {
                this.model = kendo.observable(observable);
                var dataItemFullTemp = await $.get(`${ENV.vApi}Pnrdetail/read/${Config.id}`);
                var dataItemFull = JSON.parse(dataItemFullTemp);
                dataItemFull = dataItemFull.data.Booking;
                var CurrencyCode = dataItemFull.CurrencyCode;
                dataItemFull.BookingID = '#' + dataItemFull.BookingID;
                dataItemFull.BookingInfo.CreatedDateFormatted = gridDate(new Date(dataItemFull.BookingInfo.CreatedDate), 'ddMMMyyyy HH:mm');
                dataItemFull.BookingInfo.ModifiedDateFormatted = gridDate(new Date(dataItemFull.BookingInfo.ModifiedDate), 'ddMMMyyyy HH:mm');
                dataItemFull.BookingSum.TotalCostFormated = gridNumberFormat(parseFloat(dataItemFull.BookingSum.TotalCost)) + " " + dataItemFull.CurrencyCode;
                dataItemFull.BookingSum.BalanceDueFormated = gridNumberFormat(parseFloat(dataItemFull.BookingSum.BalanceDue)) + " " + dataItemFull.CurrencyCode;
                dataItemFull.POS.OriginalAgent = dataItemFull.POS.AgentCode + "//" + dataItemFull.POS.OrganizationCode;
                if(typeof dataItemFull.Journeys.Journey.JourneySellKey !== 'undefined') {
                    var flightInfo = dataItemFull.Journeys.Journey;
                    dataItemFull.Journeys.Journey = [flightInfo];
                }
                $.each(dataItemFull.Journeys.Journey, function(key, value) {
                    value.no = key + 1;
                });
                if(typeof dataItemFull.Passengers.Passenger.Names !== 'undefined') {
                    var passengerInfo = dataItemFull.Passengers.Passenger;
                    dataItemFull.Passengers.Passenger = [passengerInfo];
                }
                dataItemFull.Passengers.total = dataItemFull.Passengers.Passenger.length;
                $.each(dataItemFull.Passengers.Passenger, function(key, value) {
                    value.no = key + 1;
                });
                if(typeof dataItemFull.Payments.Payment.AccountName !== 'undefined') {
                    var payment = dataItemFull.Payments.Payment;
                    dataItemFull.Payments.Payment = [payment];
                }
                dataItemFull.Payments.total = dataItemFull.Payments.Payment.length;
                if(typeof dataItemFull.BookingComments.BookingComment.CommentText !== 'undefined') {
                    var comment = dataItemFull.BookingComments.BookingComment;
                    dataItemFull.BookingComments.BookingComment = [comment];
                }
                this.model.set('item', dataItemFull);
                console.log(this.model.get('item'));
            },
            init: function() {
                ajaxStart();
                this.read().then(() => {
                    kendo.bind($("#pnr-detail"), this.model);
                    ajaxComplete();
                })
            }
        }
    }();
    window.onload = function() {
        ajaxStart();
        Detail.init();
    };
</script>

