<div id="pnr-detail">
    <div class="col-sm-4">
        <div class="block full">
            <div class="block-title clearfix">
                <h2>
                    <img src="data:image/svg+xml;base64,
" />
                    @OVERVIEW@</h2>
            </div>
            <div class="row">
                <h4 class="col-sm-4" style="font-weight: bold; padding-left: 0"><span data-bind="text: item.RecordLocator"></span></h4>
                <h6 class="col-sm-8"><span class="pull-right" data-bind="text: item.BookingID"></span></h6>
            </div>
            <div class="row">
                <div class="row">
                    <div class="col-sm-5"><span>@Booking@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.BookingInfo.BookingStatus"></span></div>
                </div>
                <div class="row">
                    <div class="col-sm-5"><span>@Payment@:</span></div>
                    <div class="col-sm-7"><span></span></div>
                </div>
                <div class="row">
                    <div class="col-sm-5" style="padding-left: 20px"><span> - @Total Cost@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.BookingSum.TotalCostFormated"></span></div>
                </div>
                <div class="row">
                    <div class="col-sm-5" style="padding-left: 20px"><span> - @Amount Due@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.BookingSum.BalanceDueFormated"></span></div>
                </div>
                <!--                        <div class="row">-->
                <!--                            <div class="col-sm-5"><span>@Caller@:</span></div>-->
                <!--                            <div class="col-sm-7"><span></span></div>-->
                <!--                        </div>-->
                <div class="row">
                    <div class="col-sm-5"><span>@Owning Carrier@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.BookingInfo.OwningCarrierCode"></span></div>
                </div>
            </div>
        </div>
        <div class="block full">
            <div class="block-title clearfix">
                <h2>
                    <img src="data:image/svg+xml;base64," />
                    @DETAILS@</h2>
            </div>
            <div class="row">
                <div class="row">
                    <div class="col-sm-5"><span>@Creation Date@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.BookingInfo.CreatedDateFormatted"></span></div>
                </div>
                <div class="row">
                    <div class="col-sm-5"><span>@Modified Date@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.BookingInfo.ModifiedDateFormatted"></span></div>
                </div>
                <div class="row">
                    <div class="col-sm-5"><span>@Original Agent@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.POS.OriginalAgent"></span></div>
                </div>
                <div class="row">
                    <div class="col-sm-5"><span>@Original Caller@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.ReceivedBy.ReceivedBy"></span></div>
                </div>
                <div class="row">
                    <div class="col-sm-5"><span>@Group Name@:</span></div>
                    <div class="col-sm-7"><span data-bind="text: item.POS.OrganizationCode"></span></div>
                </div>
            </div>
        </div>
        <div class="block full">
            <div class="block-title clearfix">
                <h2>
                    <img src="data:image/svg+xml;base64,
" />
                    @COST SUMMARY@</h2>
            </div>
            <div class="row">
                <div class="col-sm-5"><span>@Total Cost@:</span></div>
                <div class="col-sm-7"><span data-bind="text: item.TotalCost"></span> <span data-bind="text: CurrencyCode"></span></div>
            </div>
            <div class="row">
                <div class="col-sm-5"><span>@Amount Due@:</span></div>
                <div class="col-sm-7"><span data-bind="text: item.BalanceDue"></span> <span data-bind="text: CurrencyCode"></span></div>
            </div>
        </div>
    </div>
    <div class="col-sm-8">
        <div class="block full">
            <div class="block-title clearfix">
                <h2>
                    <img src="data:image/svg+xml;base64," />
                    @FLIGHT INFORMATION@</h2>
            </div>
            <div data-role="grid"
                 data-pageable="false"
                 data-no-records="{
                                        template: `<h2 class='text-danger'>@NO DATA@</h2>`
                                    }"
                 data-columns="[
                                        {
                                            field: 'LegNumber',
                                            title: '@Leg no.@',
                                            width: '100px'
                                        },
                                        {
                                            field: 'FlightDesignator',
                                            title: '@Flight name@',
                                            template: '#: FlightDesignator.CarrierCode #-#: FlightDesignator.FlightNumber #'
                                        },
                                        {
                                            field: 'STDDateString',
                                            title: '@Flight date@',
                                        },
                                        {
                                            field: 'Route',
                                            title: '@Route@',
                                            template: '#: DepartureStation # - #: ArrivalStation #'
                                        },
                                        {
                                            field: 'STAString',
                                            title: '@Flight time@',
                                            template: '#: STDString # - #: STAString #'
                                        },
                                        {
                                            field: 'LiftStatus',
                                            title: '@Flight status@',
                                        },
                                        ]"
                 data-bind="source: item.flightInfo"></div>
        </div>
        <div class="block full">
            <div class="block-title clearfix">
                <h2>
                    <img src="data:image/svg+xml;base64,
" />
                    @OTHER SERVICES@</h2>
            </div>
            <div class="row">
                <div class="col-sm-12"><span></span></div>
            </div>
        </div>
        <div style="display: flex">
            <div class="col-sm-6 block full" style="margin-right: 10px">
                <div class="block-title clearfix">
                    <h2>
                        <img src="data:image/svg+xml;base64," />
                        @PASSENGERS@ (<span data-bind="text: item.passengersTotal"></span>)</h2>
                </div>
                <ul style="list-style-type: none; padding-left: 0" class="main-list rectangle-list" data-template="passenger-detail" data-bind="source: item.passengerInfo"></ul>
            </div>
            <div class="col-sm-6 block full">
                <div class="block-title clearfix">
                    <h2>
                        <img src="data:image/svg+xml;base64," />
                        @CONTACT INFORMATION@</h2>
                </div>
                <div class="row" data-template="contact-detail" data-bind="source: item.BookingContacts"></div>
            </div>
        </div>
        <div style="display: flex">
            <div class="col-sm-6 block full" style="margin-right: 10px">
                <div class="block-title clearfix">
                    <h2>
                        <img src="data:image/svg+xml;base64," />
                        @PAYMENTS@ (<span data-bind="text: item.Payments.total"></span>)</h2>
                </div>
                <div class="row" data-template="payment-detail" data-bind="source: item.Payments.Payment"></div>
            </div>
            <div class="col-sm-6 block full">
                <div class="block-title clearfix">
                    <h2>
                        <img src="data:image/svg+xml;base64," />
                        @COMMENTS@</h2>
                </div>
                <div class="row" data-template="comment-detail" data-bind="source: item.BookingComments.BookingComment"></div>
            </div>
        </div>
    </div>
</div>

<script id="passenger-detail" type="text/x-kendo-template">
    <li>
        <a href="javascript:void(0)" data-bind="click: openCustomerInfo"><span class="number-count">#: no #</span><span style="font-weight: 900">#: LastName #, #: FirstName #</span> #: (typeof PaxType !== 'undefined') ? PaxType : '' # #: CurrencyCode # #: kendo.toString(parseFloat(TotalCost), '\#\#,\#') # #: CurrencyCode #</a>
        <ol class="row" style="margin-left: 40px;" data-bind="source: seatInfo" data-template="seat-and-ssrs-by-passenger-info"></ol><br />
    </li>
</script>

<script id="seat-and-ssrs-by-passenger-info" type="text/x-kendo-template">
    <li class="col-sm-6" style="margin-bottom: 10px; padding: unset">
        <span data-bind="html: htmlJourney"></span><br />
        <ol data-bind="source: ssrs" data-template="ssrs-detail-by-flight-info"></ol>
    </li>
</script>

<script id="ssrs-detail-by-flight-info" type="text/x-kendo-template">
    <li style="margin-top: 5px">
        - <span data-bind="text: SSRCode"></span>
    </li>
</script>

<script id="payment-detail" type="text/x-kendo-template">
    <div class="col-sm-12">
        <span>#: PaymentMethodCode # #: QuotedCurrencyCode # #: kendo.toString(parseFloat(PaymentAmount), "\#\#,\#") # #: QuotedCurrencyCode # #: kendo.toString(new Date(CreatedDate), "dd MMM yy") # (#: PaymentID #)</span>
    </div>
</script>

<script id="contact-detail" type="text/x-kendo-template">
    <div class="col-sm-12">
        # if(typeof BookingContact.CompanyName !== 'undefined' && BookingContact.CompanyName !== null && BookingContact.CompanyName !== '' && BookingContact.CompanyName !== ' ') { #
        <span>#: BookingContact.CompanyName #</span><br>
        # } #
        <span>#: BookingContact.Names.BookingName.FirstName # #: BookingContact.Names.BookingName.LastName #</span><br>
        # if(typeof BookingContact.AddressLine1 !== 'undefined' && BookingContact.AddressLine1 !== null && BookingContact.AddressLine1 !== '' && BookingContact.AddressLine1 !== ' ') { #
        <span>#: BookingContact.AddressLine1 #</span>
        # } #
        # if(typeof BookingContact.City !== 'undefined' && BookingContact.City !== null && BookingContact.City !== '' && BookingContact.City !== ' ') { #
        <span>#: BookingContact.City #, </span>
        # } #
        # if(typeof BookingContact.PostalCode !== 'undefined' && BookingContact.PostalCode !== null && BookingContact.PostalCode !== '' && BookingContact.PostalCode !== ' ') { #
        <span>#: BookingContact.PostalCode # </span>
        # } #
        <span>#: BookingContact.CountryCode #</span><br>
        # if(typeof BookingContact.WorkPhone !== 'undefined' && BookingContact.WorkPhone !== null && BookingContact.WorkPhone !== '' && BookingContact.WorkPhone !== ' ') { #
        <span>#: BookingContact.WorkPhone # </span><br>
        # } #
        # if(typeof BookingContact.HomePhone !== 'undefined' && BookingContact.HomePhone !== null && BookingContact.HomePhone !== '' && BookingContact.HomePhone !== ' ') { #
        <span>#: BookingContact.HomePhone # </span><br>
        # } #
        # if(typeof BookingContact.OtherPhone !== 'undefined' && BookingContact.OtherPhone !== null && BookingContact.OtherPhone !== '' && BookingContact.OtherPhone !== ' ') { #
        <span>#: BookingContact.OtherPhone # </span><br>
