    </div>
</script>

<script id="comment-detail" type="text/x-kendo-template">
    <span style="word-wrap: break-word;"> - #: CommentText #</span><br>
    <hr>
</script>

<script>
    var Detail = function() {
        var observable = Object.assign(Config.observable, {
            item: {
                pnr_code: $("#pnr-code").val(),
            },
            openCustomerInfo: function(customerID) {
                if(customerID.data.CustomerNumber !== null && customerID.data.CustomerNumber !== "") {
                    $.get(`${ENV.vApi}Customer/getCustomerByCustomerNumber/${customerID.data.CustomerNumber}`, function(data) {
                        if(data.data) {
                            window.open(`manage/customer/#/detail/${data.data.id}`,'_blank',null);
                        }
                        else {
                            notification.show(`<big>Khch hng ny hin cha c thng tin trong c s d liu!</big>`, "warning");
                        }
                    });
                }
                else {
                    notification.show(`<big>Khch hng ny hin cha c thng tin trong c s d liu!</big>`, "warning");
                }
            }
        });
        var model = kendo.observable(observable);
        return {
            model: model,
            read: async function() {
                var dataItemFullTemp = await $.get(`${ENV.vApi}Pnrdetail/readFromAPI/${Config.observable.item.pnr_code}`);
                var dataItemFull = JSON.parse(dataItemFullTemp);
                dataItemFull = dataItemFull.data.Booking;
                var CurrencyCode = dataItemFull.CurrencyCode;
                dataItemFull.BookingID = '#' + dataItemFull.BookingID;
                dataItemFull.BookingInfo.CreatedDateFormatted = gridDate(new Date(dataItemFull.BookingInfo.CreatedDate), 'ddMMMyyyy HH:mm');
                dataItemFull.BookingInfo.ModifiedDateFormatted = gridDate(new Date(dataItemFull.BookingInfo.ModifiedDate), 'ddMMMyyyy HH:mm');
                dataItemFull.BookingSum.TotalCostFormated = gridNumberFormat(parseFloat(dataItemFull.BookingSum.TotalCost)) + " " + dataItemFull.CurrencyCode;
                dataItemFull.BookingSum.BalanceDueFormated = gridNumberFormat(parseFloat(dataItemFull.BookingSum.BalanceDue)) + " " + dataItemFull.CurrencyCode;
                dataItemFull.POS.OriginalAgent = dataItemFull.POS.AgentCode + "//" + dataItemFull.POS.OrganizationCode;
                var flightInfo = [];
                var passengerInfo = [];
                var seatInfo = [];
                var ssrsInfo = [];
                var ssrsCount = 0;

                // Flight info
                if(typeof dataItemFull.Journeys.Journey.Segments.Segment === 'object') {
                    var segments = dataItemFull.Journeys.Journey.Segments.Segment;
                    var legs = segments.Legs.Leg;
                    if(typeof legs === 'object') {
                        var liftStatus = '';
                        if(typeof segments.PaxSegments.PaxSegment === 'object') {
                            liftStatus = segments.PaxSegments.PaxSegment.LiftStatus;
                        }
                        else {
                            liftStatus = segments.PaxSegments.PaxSegment[key].LiftStatus;
                        }
                        flightInfo.push({
                            ArrivalStation: legs.ArrivalStation,
                            DepartureStation: legs.DepartureStation,
                            FlightDesignator: legs.FlightDesignator,
                            LegNumber: 1,
                            LiftStatus: liftStatus,
                            STAString: gridDate(new Date(legs.STA), 'HH:mm'),
                            STDString: gridDate(new Date(legs.STD), 'HH:mm'),
                            STDDateString: gridDate(new Date(legs.STD), 'ddd, ddMMMyy')
                        });
                    }
                    else {
                        var legs = value.Legs.Leg;
                        var liftStatus = '';
                        if(typeof segments.PaxSegments.PaxSegment === 'object') {
                            liftStatus = segments.PaxSegments.PaxSegment.LiftStatus;
                        }
                        else {
                            liftStatus = segments.PaxSegments.PaxSegment[key].LiftStatus;
                        }
                        $.each(legs, function(key1, value1) {
                            flightInfo.push({
                                ArrivalStation: value1.ArrivalStation,
                                DepartureStation: value1.DepartureStation,
                                FlightDesignator: value1.FlightDesignator,
                                LegNumber: (key1 + 1),
                                LiftStatus: liftStatus,
                                STAString: gridDate(new Date(value1.STA), 'HH:mm'),
                                STDString: gridDate(new Date(value1.STD), 'HH:mm'),
                                STDDateString: gridDate(new Date(value1.STD), 'ddd, ddMMMyy')
                            });
                        });
                    }

                    // Thng tin SSRs
                    var ssrs = segments.PaxSSRs.PaxSSR;
                    if(typeof ssrs !== 'undefined') {
                        if(typeof ssrs.ActionStatusCode !== 'undefined') {
                            if(typeof ssrsInfo[ssrs.DepartureStation + '-' + ssrs.PassengerNumber] === 'undefined') {
                                ssrsInfo[ssrs.DepartureStation + '-' + ssrs.PassengerNumber] = [];
                            }
                            ssrsInfo[ssrs.DepartureStation + '-' + ssrs.PassengerNumber].push({
                                SSRCode: ssrs.SSRCode
                            });
                            ssrsCount++;
                        }
                        else {
                            $.each(ssrs, function(ssrsKey, ssrsValue) {
                                if(typeof ssrsInfo[ssrsValue.DepartureStation + '-' + ssrsValue.PassengerNumber] === 'undefined') {
                                    ssrsInfo[ssrsValue.DepartureStation + '-' + ssrsValue.PassengerNumber] = [];
                                }
                                ssrsInfo[ssrsValue.DepartureStation + '-' + ssrsValue.PassengerNumber].push({
                                    SSRCode: ssrsValue.SSRCode
                                });
                                ssrsCount++;
                            });
                        }
                    }
                    // Thng tin SSRs

                    // Thng tin gh
                    var seat = segments.PaxSeats.PaxSeat;
                    var bs_color = HELPER.bsColors;
                    if(typeof seat.ArrivalStation !== 'undefined') {
                        seatInfo[seat.PassengerNumber] = [];
                        if(seat.UnitDesignator !== null && seat.UnitDesignator !== '') {
                            var htmlJourney = `<span class="label label-${bs_color[1%6]}" style="color: #ffffff"><b style="font-weight: 900;">${seat.DepartureStation} - ${seat.ArrivalStation}</b>&nbsp;(${seat.UnitDesignator})</span>`;
                        }
                        else {
                            var htmlJourney = `<span class="label label-${bs_color[1%6]}" style="color: #ffffff"><b style="font-weight: 900;">${seat.DepartureStation} - ${seat.ArrivalStation}</b>`;
                        }
                        seatInfo[seat.PassengerNumber].push(
                            {
                                DepartureStation    : seat.DepartureStation,
                                ArrivalStation      : seat.ArrivalStation,
                                UnitDesignator      : seat.UnitDesignator,
                                htmlJourney         : htmlJourney,
                                ssrs                : (ssrsCount > 0) ? ssrsInfo[seat.DepartureStation + '-' + seat.PassengerNumber] : []
                            }
                        );
                    }
                    else {
                        $.each(seat, function (seatKey, seatValue) {
                            if(seatValue.UnitDesignator !== null && seatValue.UnitDesignator !== '') {
                                var htmlJourney = `<span class="label label-${bs_color[seatKey%6]}" style="color: #ffffff"><b style="font-weight: 900;">${seatValue.DepartureStation} - ${seatValue.ArrivalStation}</b>&nbsp;(${seatValue.UnitDesignator})</span>`;
                            }
                            else {
                                var htmlJourney = `<span class="label label-${bs_color[seatKey%6]}" style="color: #ffffff"><b style="font-weight: 900;">${seatValue.DepartureStation} - ${seatValue.ArrivalStation}</b>`;
                            }
                            if(typeof seatInfo[seatValue.PassengerNumber] === 'undefined') {
                                seatInfo[seatValue.PassengerNumber] = [];
                            }
                            seatInfo[seatValue.PassengerNumber].push(
                                {
                                    DepartureStation    : seatValue.DepartureStation,
                                    ArrivalStation      : seatValue.ArrivalStation,
                                    UnitDesignator      : seatValue.UnitDesignator,
                                    htmlJourney         : htmlJourney,
                                    ssrs                : (ssrsCount > 0) ? ssrsInfo[seatValue.DepartureStation + '-' + seatValue.PassengerNumber] : []
                                }
                            );
                        });
                    }
                    // Thng tin gh
                }
                else {
                    var segments = dataItemFull.Journeys.Journey.Segments.Segment;
                    $.each(segments, function(key, value) {
                        var legs = value.Legs.Leg;
                        if(typeof legs === 'object') {
                            var liftStatus = '';
                            if(typeof value.PaxSegments.PaxSegment === 'object') {
                                liftStatus = value.PaxSegments.PaxSegment.LiftStatus;
                            }
                            else {
                                liftStatus = value.PaxSegments.PaxSegment[key].LiftStatus;
                            }
                            flightInfo.push({
                                ArrivalStation: legs.ArrivalStation,
                                DepartureStation: legs.DepartureStation,
                                FlightDesignator: legs.FlightDesignator,
                                LegNumber: 1,
                                LiftStatus: liftStatus,
                                STAString: gridDate(new Date(legs.STA), 'HH:mm'),
                                STDString: gridDate(new Date(legs.STD), 'HH:mm'),
                                STDDateString: gridDate(new Date(legs.STD), 'ddd, ddMMMyy')
                            });
                        }
                        else {
                            var legs = value.Legs.Leg;
                            var liftStatus = '';
                            if(typeof value.PaxSegments.PaxSegment === 'object') {
                                liftStatus = value.PaxSegments.PaxSegment.LiftStatus;
                            }
                            else {
                                liftStatus = value.PaxSegments.PaxSegment[key].LiftStatus;
                            }
                            $.each(legs, function(key1, value1) {
                                flightInfo.push({
                                    ArrivalStation: value1.ArrivalStation,
                                    DepartureStation: value1.DepartureStation,
                                    FlightDesignator: value1.FlightDesignator,
                                    LegNumber: (key1 + 1),
                                    LiftStatus: liftStatus,
                                    STAString: gridDate(new Date(value1.STA), 'HH:mm'),
                                    STDString: gridDate(new Date(value1.STD), 'HH:mm'),
                                    STDDateString: gridDate(new Date(value1.STD), 'ddd, ddMMMyy')
                                });
                            });
                        }
                        // Thng tin gh
                        var bs_color = HELPER.bsColors;
                        var seat = value.PaxSeats.PaxSeat;
                        if(typeof seat.ArrivalStation !== 'undefined') {
                            if(seat.UnitDesignator !== null && seat.UnitDesignator !== '') {
                                var htmlJourney = `<span class="label label-${bs_color[1%6]}" style="color: #ffffff"><b style="font-weight: 900;">${seat.DepartureStation} - ${seat.ArrivalStation}</b>&nbsp;(${seat.UnitDesignator})</span>`;
                            }
                            else {
                                var htmlJourney = `<span class="label label-${bs_color[1%6]}" style="color: #ffffff"><b style="font-weight: 900;">${seat.DepartureStation} - ${seat.ArrivalStation}</b>`;
                            }
                            seatInfo[seat.PassengerNumber] = [];
                            seatInfo[seat.PassengerNumber].push(
                                {
                                    DepartureStation    : seat.DepartureStation,
                                    ArrivalStation      : seat.ArrivalStation,
                                    UnitDesignator      : seat.UnitDesignator,
                                    htmlJourney         : htmlJourney,
                                    ssrs                : (ssrsCount > 0) ? ssrsInfo[seat.DepartureStation + '-' + seat.PassengerNumber] : []
                                }
                            );
                        }
                        else {
                            $.each(seat, function (key1, value1) {
                                if(value1.UnitDesignator !== null && value1.UnitDesignator !== '') {
                                    var htmlJourney = `<span class="label label-${bs_color[key1%6]}" style="color: #ffffff"><b style="font-weight: 900;">${value1.DepartureStation} - ${value1.ArrivalStation}</b>&nbsp;(${value1.UnitDesignator})</span>`;
                                }
                                else {
                                    var htmlJourney = `<span class="label label-${bs_color[key1%6]}" style="color: #ffffff"><b style="font-weight: 900;">${value1.DepartureStation} - ${value1.ArrivalStation}</b>`;
                                }

                                seatInfo[value1.PassengerNumber] = [];
                                seatInfo[value1.PassengerNumber].push(
                                    {
                                        DepartureStation    : value1.DepartureStation,
                                        ArrivalStation      : value1.ArrivalStation,
                                        UnitDesignator      : value1.UnitDesignator,
                                        htmlJourney         : htmlJourney,
                                        ssrs                : (ssrsCount > 0) ? ssrsInfo[value1.DepartureStation + '-' + value1.PassengerNumber] : []
                                    }
                                );
                            });
                        }
                        // Thng tin gh
                    });
                }
                dataItemFull.flightInfo = flightInfo;
                // Flight info

                // Passenger info
                var passengerInfo = [];
                var infoTemp = dataItemFull.Passengers.Passenger;
                if(typeof infoTemp.Names !== 'undefined') {
                    passengerInfo.push({
                        no              : 1,
                        PassengerNumber : infoTemp.PassengerNumber,
                        FirstName       : infoTemp.Names.BookingName.FirstName,
                        MiddleName      : infoTemp.Names.BookingName.MiddleName,
                        LastName        : infoTemp.Names.BookingName.LastName,
                        TotalCost       : infoTemp.PassengerInfo.TotalCost,
                        CurrencyCode    : dataItemFull.CurrencyCode,
                        CustomerNumber  : infoTemp.CustomerNumber,
                        seatInfo        : seatInfo[infoTemp.PassengerNumber]
                    });
                }
                else {
                    $.each(infoTemp, function(key, value) {
                        passengerInfo.push({
                            no              : key + 1,
                            PassengerNumber : value.PassengerNumber,
                            FirstName       : value.Names.BookingName.FirstName,
                            MiddleName      : value.Names.BookingName.MiddleName,
                            LastName        : value.Names.BookingName.LastName,
                            TotalCost       : value.PassengerInfo.TotalCost,
                            CurrencyCode    : dataItemFull.CurrencyCode,
                            CustomerNumber  : value.CustomerNumber,
                            seatInfo        : seatInfo[value.PassengerNumber]
                        });
                    });
                }
                dataItemFull.passengerInfo = passengerInfo;
                dataItemFull.passengersTotal = passengerInfo.length;
                console.log(passengerInfo);
                // Passenger info

                if(typeof dataItemFull.Payments.Payment.AccountName !== 'undefined') {
                    var payment = dataItemFull.Payments.Payment;
                    dataItemFull.Payments.Payment = [payment];
                }
                dataItemFull.Payments.total = dataItemFull.Payments.Payment.length;
                if(typeof dataItemFull.BookingComments.BookingComment.CommentText !== 'undefined') {
                    var comment = dataItemFull.BookingComments.BookingComment;
                    dataItemFull.BookingComments.BookingComment = [comment];
                }
                this.model.set('item', dataItemFull);
                return dataItemFull;
            },
            init: function() {
                this.read().then(() => {
                    kendo.bind($("#pnr-detail"), this.model);
                })
            }
        }
    }();
    Detail.init();

    function findPNR(e) {
        var pnr_code = $("#pnr-code").val();
        router.navigate(`/detail/${pnr_code}`);
        e.preventDefault();
    }
</script>

